--     
CREATE TABLE dbo.empt (
  name          VARCHAR(50),
  salary        INT,
  gender        VARCHAR(1),
  dno           INT
)
WITH (DISTRIBUTION = ROUND_ROBIN,
      HEAP
);

--  
COPY INTO dbo.empt
FROM 'https://class2503datalake.blob.core.windows.net/employees/emp1.csv'
WITH (
  FILE_TYPE = 'CSV',
  FIRSTROW = 2
);
select * from dbo.empt;

TRUNCATE TABLE dbo.emps;
